import React, { Component } from "react";



class Folder extends Component {
  constructor() {
    super();

    this.state = {
      hidden: false,
    };

  }



  clickToggle = () => {
    this.state.hidden === true ? this.setState({ hideFolders: false }) : this.setState({ hidden: true });
  };

  render() {


    let folders = this.state.hidden === true ? (<div>
    </div>) : (
      <div>
        <ul>
          <li>File 1</li>
          <li>File 2</li>
          <li>File 3</li>
        </ul>
      </div>
    );
    return (
      <div className="folder-div">
        <h1>Home</h1>
        {folders}
        <button onClick={this.clickToggle}>Toggle</button>
      </div>
    );
  }
}

export default Folder;
